INSERT INTO places (id, name, description, type_id, urls, lat, lng) VALUES
(1, 'Государственный Эрмитаж', 'Один из крупнейших и самых известных художественных музеев мира, расположенный в Санкт-Петербурге. Его коллекции охватывают историю мирового искусства от древнейших времен до наших дней. Здесь можно увидеть шедевры живописи, скульптуры, графики, декоративно-прикладного искусства, археологические находки и многое другое. Эрмитаж – это не только музей, но и культурный центр мирового значения.', 5, 'https://avatars.mds.yandex.net/i?id=5971d0f2794959dfc3f8eedbcbed0ef4a2981fcf-16342274-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=b06f1d6a70ef2701a9c3ec079649e079b73b48fd-5866281-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=e264eace00a78b4c44609ed778472f60580ec41e-5288592-images-thumbs&n=13', 59.939864, 30.314566),
(2, 'Парк Горького', 'Центральный парк Москвы, одно из самых популярных мест отдыха горожан и туристов. Здесь есть аттракционы, зоны отдыха, набережная Москвы-реки, катки зимой и многое другое. В парке проводятся фестивали, концерты, выставки и другие мероприятия. Это отличное место для прогулок, занятий спортом и просто приятного времяпровождения на свежем воздухе в центре мегаполиса. Велосипеды и самокаты можно взять в аренду.', 2, 'https://avatars.mds.yandex.net/i?id=6f761eb72c59fd200c9758825284bbb512d58cd7-16482085-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=8fe47da00cb1bb8bc3944973b64a8593ea20338d-4156995-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=2a000001994c58f26237bd54d76bbb8fb772-1439138-fast-images&n=13', 55.731395, 37.603382),
(3, 'Кафе Пушкинъ', 'Известное московское кафе с изысканной кухней и неповторимой атмосферой. Интерьер кафе стилизован под дворянскую усадьбу XIX века. В меню представлены блюда русской и французской кухни. Кафе славится своими десертами и выпечкой. Это прекрасное место для романтического ужина, деловой встречи или просто приятного отдыха в элегантной обстановке. Здесь вы окунетесь в атмосферу роскоши и комфорта.', 9, 'https://avatars.mds.yandex.net/i?id=21f39fd99eddc90621363e50076cb06fbdc3979d-3563775-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=4ea5c993c0ec55419c26458e954a65acfb068ef2-10163806-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=08262daf2b8a8d627327ea4576e222c7a3227ec5-12614867-images-thumbs&n=13', 55.763700, 37.605041),
(4, 'Ресторан White Rabbit', 'Ресторан с панорамным видом на Москву, расположенный на 16 этаже Смоленского пассажа. В меню представлена современная русская кухня с использованием локальных продуктов. Ресторан входит в число лучших ресторанов мира. Это отличное место для особого случая, романтического ужина или просто чтобы насладиться вкусной едой и потрясающими видами на столицу с высоты птичьего полета. Бронируйте столик заранее!', 8, 'https://avatars.mds.yandex.net/i?id=7ff4cf1beedee961d4314eb123205e357d5e4c9d-5101235-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=cccad97c803b40334dc6bc1c4be89c9921008547-16187736-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=57de91debd0c08ae98c6427b0ce4a24f32e2b194-5876235-images-thumbs&n=13', 55.747485, 37.581307),
(5, 'Третьяковская галерея', 'Музей русского изобразительного искусства в Москве, где собрана богатейшая коллекция икон, живописи, графики и скульптуры. Здесь можно увидеть шедевры русских художников от древности до наших дней, включая «Троицу» Андрея Рублева, «Явление Христа народу» Александра Иванова и многие другие известные произведения. Это место, где можно прикоснуться к истории и культуре России, насладиться красотой и талантом русских мастеров.', 5, 'https://avatars.mds.yandex.net/i?id=37618e5ef8e3aa56bd3e37560b6c832b4db15a8b-8922087-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=c05600be624ae7ed21d661069e8bacd0e62972b4-5760374-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=1e4c213d36828fe2562fc71e2de92157d1c69719-4885535-images-thumbs&n=13', 55.741370, 37.620740),
(6, 'Александровский сад', 'Парк в центре Москвы, расположенный вдоль Кремлевской стены. Здесь можно увидеть грот «Руины», памятник Александру I, могилу Неизвестного солдата и смену караула. Это тихое и уютное место для прогулок и отдыха в самом сердце столицы. Сад украшен цветами, фонтанами и скульптурами. Здесь можно отдохнуть от городской суеты, насладиться природой и ощутить связь с историей России.', 2, 'https://avatars.mds.yandex.net/i?id=bb7810491c4be900748493c4bf955ab4e3ef240b-8899644-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=e19e102077eb1584017a4f0fee018f7db29c6125-16283092-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=a3cfebc9ae10fb85339fb6e1b7017ef43a08ce7c-12480042-images-thumbs&n=13', 55.751421, 37.613359),
(7, 'Кафе Dr. Живаго', 'Кафе в центре Москвы с русской кухней и видом на Кремль. Интерьер кафе стилизован под советскую эпоху. В меню представлены традиционные русские блюда в современной интерпретации. Это отличное место для знакомства с русской кухней и культурой. Здесь можно попробовать пельмени, борщ, пироги и другие блюда, приготовленные по старинным рецептам. А также насладиться прекрасным видом на исторический центр Москвы.', 9, 'https://avatars.mds.yandex.net/i?id=1e82442a586b4fcd4af3fbc9e6ec41e3ebdaee09-16443563-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=1f07cf650291eb53a0d34798faaefd909de633ff-5384958-images-thumbs&n=13', 55.756736, 37.613997),
(8, 'Ресторан Savva', 'Ресторан в отеле «Метрополь» с современной русской кухней. В меню представлены авторские блюда, приготовленные из сезонных продуктов. Ресторан славится своей элегантной обстановкой и высоким уровнем сервиса. Это прекрасное место для особого случая или делового обеда. Здесь вы сможете насладиться изысканными блюдами, созданием известными шеф-поварами, и окунуться в атмосферу роскоши и комфорта исторического отеля.', 8, 'https://avatars.mds.yandex.net/i?id=b3298abe7aabca6faf5c31e32991d37a600bbc3c-4476958-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=2c5fe152741840dfe7f5d57b9d53aa4fdbfba07f-8411661-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=8c4934715fcb25b13a13d7475e82cbddb7cea4b5-4538830-images-thumbs&n=13', 55.758448, 37.621669),
(9, 'Собор Василия Блаженного', 'Православный храм на Красной площади, символ Москвы и России. Собор поражает своей уникальной архитектурой и ярким декором. Внутри собора расположен музей, где можно узнать об истории храма и увидеть старинные иконы. Это одно из самых известных и узнаваемых зданий в мире, которое обязательно стоит посетить каждому гостю Москвы. Осмотритесь вокруг, оцените масштабы и величие архитектуры!', 6, 'https://avatars.mds.yandex.net/i?id=6d10f12be1e5e08a2bd37aab61aeb48c564faba2-10637407-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=9338e190407096d1ef5ab8e78f27e6a448ad9466d3a72134-9858395-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=71775a5ad53cf1dd4dbadba63e00bd02aeaeda99-16457393-images-thumbs&n=13', 55.752639, 37.622721),
(10, 'Музей современного искусства «Гараж»', 'Музей современного искусства в Парке Горького, ориентированный на изучение и продвижение современного искусства и культуры. Здесь можно увидеть работы как известных, так и начинающих художников, а также принять участие в образовательных программах, лекциях и мастер-классах. Музей стремится быть открытым и доступным для всех, интересующихся современным искусством. Изучите экспозицию и узнайте что-то новое!', 5, 'https://avatars.mds.yandex.net/i?id=88069ac7ee69bdb0d99510f27254efdd18958795-16971703-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=6f04d15abeb68af6cbfd25955bae8931ca3e783f-4348031-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=98e34e811e1ed6ed0c88e1aef9dafb54075f2180-9068918-images-thumbs&n=13', 55.727596, 37.601630),
(11, 'Калининградский зоопарк', 'Один из самых старых и больших зоопарков России. Здесь обитают разнообразные животные со всего мира, от крупных хищников до экзотических птиц и рептилий. Зоопарк является не только местом для развлечения, но и научно-исследовательским центром, занимающимся изучением и сохранением редких и исчезающих видов животных. Посещение Калининградского зоопарка – это увлекательное путешествие в мир дикой природы и возможность узнать больше о животных и их среде обитания.', 1, 'https://avatars.mds.yandex.net/i?id=9501f3963a11213588da39c8d3c6a7dabb3300d4-4356414-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=1b2135dc4182ac75446a0669d6443255c2251da3-16341963-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=2dba4d7e19b90f3404214ea71ae7b49e39871b5d-8485874-images-thumbs&n=13', 54.720345, 20.488263),
(12, 'Большой театр', 'Крупнейший в России театр оперы и балета в Москве. Его история насчитывает более 200 лет. Он – символ русской культуры.', 4, 'https://avatars.mds.yandex.net/i?id=d6136b1cc2d4332a82845c19e593c0f08e1d3eec-5423691-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=95d1af18a13c2d5180bf29866a2a59c7c5fad500-16451811-images-thumbs&n=13', 55.759714, 37.618819),
(13, 'Памятник Петру I («Медный всадник»)', 'Знаменитый памятник Петру I на Сенатской площади в Санкт-Петербурге, являющийся одним из самых известных символов города.', 3, 'https://avatars.mds.yandex.net/i?id=4999376e16fbd7e22215d105756cfb45baaf1868-13097247-images-thumbs&n=13,https://avatars.mds.yandex.net/i?id=d89bd104ec950cd23797b7f7f3f746898180226d-16487771-images-thumbs&n=13', 59.936392, 30.302211);
